<script>
    $(document).ready(function() {
        const IS_MOBILE = "0"
        const TRACKING_API = window.__ddAnalyticsAPI || `https://${window.hostWWW}.dek-d.com/analytics/`
        const COUPON_ACTIVE = ""
        const USER_ID = "0";
        const PAGE_TRACKING = "cover";
        const EXPRIES_IN = "";
        const EVENT_TRACKING = {
            VIEW: 'view-banner',
            CLICK: 'click-banner',
        }
        const PROGRAM_TRACKING = 'campaign-100-coin-app-install'
        const COOKIE_VIEW_UNIQUE = `__view-coupon-install-unique-${USER_ID}`
        const COOKIE_VIEW_FIRST = `__view-coupon-install-first-${USER_ID}`
        const COOKIE_CLICK_UNIQUE = `__click-coupon-install-unique-${USER_ID}`
        const COOKIE_CLICK_FIRST = `__click-coupon-install-first-${USER_ID}`
        const EXPRIES_TODAY = new Date(new Date().getFullYear(), new Date().getMonth(), new Date().getDate(), 23, 59, 59)
        const EXPRIES_COUPON = !!EXPRIES_IN ? new Date(EXPRIES_IN) : new Date(new Date().getFullYear(), 11, 31, 0, 0, 0)
        if (analytics && analytics.track && +USER_ID > 0 && +IS_MOBILE === 1 && +COUPON_ACTIVE === 1) {
            setTimeout(() => {
                // View Banner Event Tracking
                analytics('setApi', TRACKING_API)
                analytics('track', EVENT_TRACKING.VIEW, {
                    program: PROGRAM_TRACKING,
                    'view-page': PAGE_TRACKING,
                });
                // View Banner expired 1 day
                if (+getCookie(COOKIE_VIEW_UNIQUE) !== 1) {
                    analytics('setApi', TRACKING_API)
                    analytics('track', EVENT_TRACKING.VIEW, {
                        program: PROGRAM_TRACKING,
                        'view-page-unique': PAGE_TRACKING,
                    })
                    setCookie(COOKIE_VIEW_UNIQUE, 1, EXPRIES_TODAY)
                }
                // View Banner expired last day of year
                if (+getCookie(COOKIE_VIEW_FIRST) !== 1) {
                    analytics('setApi', TRACKING_API)
                    analytics('track', EVENT_TRACKING.VIEW, {
                        program: PROGRAM_TRACKING,
                        'view-page-first': PAGE_TRACKING,
                    })
                    setCookie(COOKIE_VIEW_FIRST, 1, EXPRIES_COUPON)
                }
            }, 100)
            $('.banner_section').click(function() {
                setTimeout(() => {
                    // Click Banner Event Tracking
                    analytics('setApi', TRACKING_API)
                    analytics('track', EVENT_TRACKING.CLICK, {
                        program: PROGRAM_TRACKING,
                        'click-page': PAGE_TRACKING,
                    })
                    // Click Banner expired 1 day
                    if (+getCookie(COOKIE_CLICK_UNIQUE) !== 1) {
                        analytics('setApi', TRACKING_API)
                        analytics('track', EVENT_TRACKING.CLICK, {
                            program: PROGRAM_TRACKING,
                            'click-page-unique': PAGE_TRACKING,
                        })
                        setCookie(COOKIE_CLICK_UNIQUE, 1, EXPRIES_TODAY)
                    }
                    // Click Banner expired last day of year
                    if (+getCookie(COOKIE_CLICK_FIRST) !== 1) {
                        analytics('setApi', TRACKING_API)
                        analytics('track', EVENT_TRACKING.CLICK, {
                            program: PROGRAM_TRACKING,
                            'click-page-first': PAGE_TRACKING,
                        })
                        setCookie(COOKIE_CLICK_FIRST, 1, EXPRIES_COUPON)
                    }
                }, 100)
            });
        }

        function setCookie(cname, cvalue, dateExp) {
            let expires = "expires=" + dateExp.toUTCString();
            document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
        }

        function getCookie(cname) {
            let name = cname + "=";
            let decodedCookie = decodeURIComponent(document.cookie);
            let ca = decodedCookie.split(';');
            for (let i = 0; i < ca.length; i++) {
                let c = ca[i];
                while (c.charAt(0) == ' ') {
                    c = c.substring(1);
                }
                if (c.indexOf(name) == 0) {
                    return c.substring(name.length, c.length);
                }
            }
            return "";
        }
    })
</script></div>
