<script type="text/javascript">
    (function () {
        let isWriter = (+'9788364' === +'0');
        let canReview;

        $(document).ready(function () {
            canReview = false;
            const reviewModal = modalBox.create('review-modal', document.querySelector('#review-modal'), {});
            const $reviewModal = document.querySelector('#review-modal');

            let $reviewLength = document.querySelector('#review-modal .modal-body .review-length');
            $reviewLength.textContent = formatNumber(document.newReview.review.value.length);

            let $inputField = document.querySelectorAll('form[name="newReview"] input');
            const $reviewForm =  document.querySelector('form[name="newReview"]');

            checkReviewForm($reviewForm);
            $inputField.forEach(field => {
            field.addEventListener('keydown', (e) => {
                if (e.keyCode === 13) {
                    e.preventDefault();
                }
            });
            });

        $('form[name*="newReview"]')
                .on('input', function (e) {
                checkReviewForm(this)
            });

            $('body')
                .on('isLogin', function (e, data) {
                    if (+data.user_id !== +'9788364') {
                        canReview = true;
                    }
                    if (+'9788364' === +data.user_id) {
                        isWriter = true;
                    }
                })
                .on('isLogout', function () {
                    canReview = false;
                    isWriter = false;
                });

            $(document).on('click', '#send-review', function (e) {
                let error = '';
                let score = document.newReview.score.value;

                if (score === '') {
                    error += '- กรุณาให้คะแนน (ดาว)';
                }

                if ($('#title').val().length < 2) {
                    error += '\n- หัวข้อจะต้องยาวไม่น้อยกว่า 2 ตัวอักษร';
                }
                if ($('#novel-review').val().length < 300) {
                    error += '\n- คำนิยมจะต้องยาวไม่น้อยกว่า 300 ตัวอักษร (ความยาวขณะนี้ : ' + $('#novel-review').val().length + ' ตัวอักษร)';
                }
                if ($('#novel-review').val().length > 1000) {
                    error += '\n- คำนิยมจะต้องยาวไม่เกิน 1,000 ตัวอักษร (ความยาวขณะนี้ : ' + $('#novel-review').val().length + ' ตัวอักษร)';
                }

                if (error.length > 0) alert(error);
                else {
                    $.ajax({
                        type: 'POST',
                        url: 'addReviewX.php',
                        data: $('form[name="newReview"]').serialize(),
                        success: function () {
                            alertBox.show('success', 'ส่งคำนิยมเรียบร้อยแล้ว', {
                                autoCloseDelay: 1000,
                                noCloseButton: true,
                                callbackClosed: function () {
                                    modalBox.get('review-modal').hide();
                                    location.reload();
                                }
                            });
                        },
                    });

                }
            })
        });

        function toggleReviewModal() {
            if (canReview) {
                modalBox.get('review-modal').show();
            } else if (isWriter) {
                alertBox.show('error', 'เจ้าของเรื่องไม่สามารถเขียน Review ได้')
            } else {
                    let pathname = window.location.pathname;
                    let search = window.location.search;
                    window.location.href = `https://www.dek-d.com/member/login?refer=${pathname}${search}`
                    return false
                }
        }

        function checkReviewForm($form) {
        let $sendButton = document.querySelector('#send-review');
        const $counterNum = document.querySelector('.review-modal .modal-body .review-length');
        const $counter = document.querySelector('.review-modal .modal-body .length');
        $counterNum.textContent = formatNumber($form.review.value.length);

        if ($form.review.value.length >= 300 && $form.review.value.length <= 1000) {
            $counter.classList.remove('-red');
        } else {
            $counter.classList.add('-red');
        }

        if (($form.score.value !== '' && $form.review.value.length >= 300) && $form.title.value.length >= 2 && $form.review.value.length <= 1000) {
            $sendButton.classList.remove('-disabled');
        } else {
            $sendButton.classList.add('-disabled');
        }
        }

        const formatNumber = function (num) {
            return num.toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')
        };
    })();
