<script>
    function isElementDisplayedWhileScrolling(elementId) {
        var element = document.getElementById(elementId);
        if (element) {
            var rect = element.getBoundingClientRect();
            var windowHeight = window.innerHeight || document.documentElement.clientHeight;
            var windowWidth = window.innerWidth || document.documentElement.clientWidth;

            // ตรวจสอบว่ามีการแสดงผลในขณะที่มีการ scroll หรือไม่
            if (rect.top >= 0 && rect.left >= 0 && rect.bottom <= windowHeight && rect.right <= windowWidth) {
                return false; // พบ Element และกำลังแสดงผล
            }
        }
        return true; // ไม่พบ Element หรือถูกซ่อนอยู่
    }
