            const tag = text.substring(0, text.length - 1)
            if (firebase && firebase.analytics()) {
                firebase.analytics().logEvent('dd_search', {
                    section: 'cover-tag',
                    search_term: tag
                })
                if (e.currentTarget.href?.includes('campaign/tag')) {
                    firebase.analytics().logEvent('dd_search', {
                        section: 'cover-tag',
                        dd_campaign_id: '',
                        search_term: tag
                    })
                }
            }
        })
    })

    function isFavoriteAction(eventTarget = null) {
        let userId = '0'
        const pathname = window.location.pathname
        const search = window.location.search
        const $recommendElement = $('#writer-feed-list')
        if (userId <= 0) {
            window.location.href = `https://www.dek-d.com/member/login?refer=https://writer.dek-d.com${pathname}${search}`
            return false
        }
        showLoadingButton()
        $.ajax({
            type: 'post',
            url: 'https://writer.dek-d.com/dekdee/writer/favorite_novel.php',
            dataType: 'json',
            data: {
                novel_id: '2485901',
                user_id: userId
            },
            xhrFields: {
                withCredentials: true
            },
            success: function(data) {
                if (data['IsErr']) {
                    alertBox.show('error', data['result_msg'])
                    toggleFavButton(false)
                    window.isFavNovel = false
                } else if (!data['IsUnFav']) {
                    const statName = window.isWebtoon ? "dd_favorite_webtoon" : "dd_favorite";
                    if (eventTarget &&
                        ($(eventTarget).is('.video-fav') || $(eventTarget).closest('.video-fav').length)) {
                        firebase.analytics().logEvent(statName, {
                            page: 'video-promote',
                            section: "video-promote-footer",
                            novel_id: '2485901',
                            main: '2',
                            sub: '2',
                        })
                    } else {
                        let page = window.readerPage;
                        if (window.isWebtoon) {
                            page = 'webtoon-' + page;
                        }
                        firebase.analytics().logEvent(statName, {
                            page: page,
                            novel_id: '2485901',
                            main: '2',
                            sub: '2',
                        })
                    }
                    alertBox.show('success', 'ติดตามเรื่องนี้เรียบร้อยแล้ว')
                    toggleFavButton(true)
                    window.isFavNovel = true
                    if (window._togglePrefaceToolbarFavButton) {
                        window._togglePrefaceToolbarFavButton(true)
                    }
                    addEventSimilar($recommendElement)
                    $('.recommend-similar-box').show()
                    pixelTrackFavoriteCategory({
                        productType: 'novel',
                        categoryId: '',
                        mainId: '2',
                        subId: '2',
                        novelId: '2485901',
                        userId: userId,
                    })
                } else if (data['IsUnFav']) {
                    alertBox.show('success', 'ยกเลิกติดตามเรื่องนี้เรียบร้อยแล้ว')
                    toggleFavButton(false)
                    window.isFavNovel = false
                    if (window._togglePrefaceToolbarFavButton) {
                        window._togglePrefaceToolbarFavButton(false)
                    }
                }
            },
            error: function() {
                toggleFavButton(false)
                window.isFavNovel = false
                alertBox.show('error', 'เกิดข้อผิดพลาด กรุณาลองใหม่อีกครั้งในภายหลัง')
            }
        })

    }

