		function initDiscountCountdown(campaignEndedAt, priceText , id) {
			if (!campaignEndedAt) return
			const countDownDate = new Date(campaignEndedAt).getTime()
			let countdownInterval = setInterval(() => {
				const nowDate = new Date().getTime()
				const distance = countDownDate - nowDate
				const days = Math.floor(distance / (1000 * 60 * 60 * 24))
				const hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60))
				const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60))
				const seconds = Math.floor((distance % (1000 * 60)) / 1000)
				let discountCountdownState = {
					days: days,
					hours: `${hours < 10 ? '0' : ''}${hours}`,
					minutes: `${minutes < 10 ? '0' : ''}${minutes}`,
					seconds: `${seconds < 10 ? '0' : ''}${seconds}`,
				}
				$('.ebook-swiper-slide').find('.discount-timeout').html(`${clockIcon}${days > 0 ? ` ${discountCountdownState.days} วัน`:''} ${discountCountdownState.hours}:${discountCountdownState.minutes}:${discountCountdownState.seconds}`)
				if (distance < 0) {
					clearInterval(countdownInterval)
					$(`#${id}.ebook-swiper-slide`).find('.discount-badge').remove()
					$(`#${id}.ebook-swiper-slide`).find('.discount-timeout').remove()
					$(`#${id}.ebook-swiper-slide`).find('.price.-discount').remove()
					$(`#${id}.ebook-swiper-slide .action`).html(`<span class="price">${coinIcon}${priceText}</span>`)
				}
			}, 1000)
		}
