    (function() {
        let oneTimeRender = true
        let othersSwiper
        let allNovels = []
        let page = 1
        let isLastPage = false
        const renderTargetId = '#others-novel-list-target'
        const renderTarget = document.querySelector(renderTargetId)
        const listTemplate = document.querySelector('#others-novel-list')
        const listLoadingTemplate = document.querySelector('#others-novel-list-loading')
        const infiniteLoadingUIClass = '-infinite-loading'
        const listLoadingUI = `
               <div class="writer-swiper-wrapper swiper-slide ${infiniteLoadingUIClass}">
                    <div class="cover"></div>
                    <div class="detail-wrapper">
                        <div class="category"></div>
                        <div class="title"></div>
                        <div class="author"></div>
                    </div>
                </div>
            `

        function _mapCategory (category) {
            return {
                mainName: category.mainTitle ? category.mainTitle : 'ฟรีสไตล์',
                subName: category.subTitle ? category.subTitle : 'อื่นๆ',
                main: category.main ? category.main : 0,
                sub: category.sub ? category.sub : -1
            }
        }

        function _mapOwnerName (owners) {
            return owners[0] ? owners[0].alias : ''
        }

        function _mapThumbnail (thumbnail) {
            return thumbnail.normal ? thumbnail.normal.replace('t_', '') : ''
        }

        function addList ({ id, category, owners, thumbnail, title }) {
            const listUI = `
                <div class="writer-swiper-wrapper swiper-slide js-others-item" data-id="${id}" data-mc="${_mapCategory(category).main}" data-sc="${_mapCategory(category).sub}">
                    <a href="view.php?id=${id}" target="_blank"
                       title="${title}">
                        <div class="cover" style="
                            background-image: url('${_mapThumbnail(thumbnail)}');
                            background-repeat: no-repeat; background-size: cover; background-position: center;
                            ">
                        </div>
                        <div class="detail-wrapper">
                            <div class="category">
                                ${_mapCategory(category).subName}
                            </div>
                            <div class="title" title="${title}">
                                ${title}
                            </div>
                            <div class="author">
                                ${_mapOwnerName(owners)}
                            </div>
                        </div>
                    </a>
                </div>
                `

            if (page === 1) {
                listTemplate.content.querySelector('.swiper-wrapper').insertAdjacentHTML('afterbegin', listUI)
            } else {
                document.querySelector(`${renderTargetId} .swiper-wrapper`).insertAdjacentHTML('beforeend', listUI)
            }
        }

        function getNovelByOwner () {
            if (isLastPage) return
            let totalLists = 0
            const Http = new XMLHttpRequest()
            const url = `https://${window.hostWWW}.dek-d.com/api/rest/novel/list?type=user&page=${page}&userId=${window.ownerId}`
            Http.open('GET', url)
            Http.send()
            Http.onreadystatechange = function (e) {
                if (this.readyState === 4 && this.status === 200) {
                    const res = JSON.parse(Http.response)
                    const lists = res.list ? res.list.filter((novel) => {
                        return novel.state.ban === 'none'
                    }) : []
                    const pageInfo = res.pageInfo ? res.pageInfo : {}
                    const { totalItems, hasNext } = pageInfo
                    const infiniteLoadingUI = document.querySelector(`${renderTargetId} .${infiniteLoadingUIClass}`)
                    isLastPage = !hasNext
                    totalLists = totalItems || 0
                    lists.sort((a, b) => new Date(b.updatedAt) - new Date(a.updatedAt)).reverse()
                    allNovels = [...allNovels, ...lists].filter((novel) => novel.id !== window.novelId)
                    infiniteLoadingUI && infiniteLoadingUI.remove()
                    if (allNovels.length <= 0) {
                        renderTarget.style.display = 'none'
                    }
                    if (page === 1) {
                        allNovels.map((novel) => {
                            addList(novel)
                        })
                        while (renderTarget.lastElementChild) {
                            renderTarget.removeChild(renderTarget.lastElementChild)
                        }
                        renderTarget.appendChild(listTemplate.content.cloneNode(true))
                        othersSwiper = new Swiper('#others-swiper', {
                            slidesPerView: 'auto',
                            spaceBetween: 10,
                            slidesOffsetBefore: 0,
                            slidesOffsetAfter: 0,
                            slidesPerGroup: 1,
                            navigation: {
                                nextEl: '#others-next',
                                prevEl: '#others-prev',
                            },
                        })
                    }
                }
            }
        }

        function isScrolledIntoView($elem) {
		    let docViewTop = $(window).scrollTop()
		    let docViewBottom = docViewTop + $(window).height()
		    let elemTop = $elem.offset().top
		    let elemBottom = elemTop + $elem.height()
		    return ( ( elemBottom <= docViewBottom ) && ( elemTop >= docViewTop ) )
	    }


        $(window).scroll(function() {
	    	if (isScrolledIntoView($(renderTargetId)) && oneTimeRender ) {
	    		renderTarget.appendChild(listLoadingTemplate.content.cloneNode(true))
                getNovelByOwner()
			    oneTimeRender = false
            }
        })
    })()
