					    $('#suggest-list').remove()
				    }
			    },
			    error: function () {
				    $('#suggest-list').remove()
			    }
		    })
	    }

	    $(document).ready(function() {
		    ajaxSetScoreAlsoView()
        })

	    $(window).scroll(function() {
	    	if (isScrolledIntoView($('#js-anchor-suggest-list')) && scrollEventOneTime ) {
	    		ajaxSuggestLists()
			    scrollEventOneTime = false
            }
        })

    })()
</script>
