	    $(window).scroll(function() {
	    	if (isScrolledIntoView($('#js-anchor-suggest-list')) && scrollEventOneTime ) {
	    		ajaxSuggestLists()
			    scrollEventOneTime = false
            }
        })

    })()
</script>
