        function appendEbookList(ebook, extension) {
            const {
                name,
                novelTitle,
                thumbnail,
                id,
                category,
                owners,
                novelId
            } = ebook
            const {
                order,
                discount,
                discountRate,
                sellingPrice,
                originalPrice,
                isTop,
                isDiscount,
            } = extension
            const {
                main,
                sub,
