                subTitle,
            } = category

            $('#ebook-rand-list-swiper .swiper-wrapper').append(`
               <div class="writer-swiper-wrapper swiper-slide js-ebook-item" data-id="${id}" data-mc="${main}" data-sc="${sub}">
