            $.ajax({
                url: `https://www.dek-d.com/api/rest/novel/recommend/list`,
                type: 'GET',
                xhrFields: {
                    withCredentials: true
                },
                data: {
                    novelId: '2485901',
                    page: 1,
			    },
                beforeSend: function() {
