                    page: isCoverPage ? 'cover' : 'reader',
                    section: `ebook-${isDiscount ? 'discount' : 'bestseller'}`,
                    type_card: `ebook-${isDiscount ? 'discount' : 'bestseller'}`,
                    novel_id: novelId,
                    ebook_id: ebookId,
                    main: mc,
                    sub: sc,
                });
            }

            window.open(href, '_blank');
        });

    })()
